define(['./_setup'], function (_setup) {
  function chunk(array, count) {
    if (count == null || count < 1) return [];
    var result = [];
    var i = 0, length = array.length;
    while (i < length) {
      result.push(_setup.slice.call(array, i, i += count));
    }
    return result;
  }
  return chunk;
});
